package java_programs;

public class addxyz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="abca";
		s=s+"xyz";
		//
	
		int n=(s.length()-1)/2;
		
	for(int i=0;i<n;i++)
	{
 	System.out.print(s.charAt(i)+""+s.charAt(n+i));

}
	System.out.println(s.charAt(s.length()-1));

	}}
