package java_programs;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int a[]= {1,2,3};
	for(int i=0;i<a.length;i++)
	{
		for(int j=i+1;j<a.length;j++)
		{
			
			if(a[i]>a[j])
			
			{
				
				int temp=a[i];
				a[j]=a[i];
				a[j]=a[i];
				
				
			}			
		}
		if(a[i]==2)
		{
		System.out.println(a[i]);
	}}
	
	}

}
